import { all, takeLatest, put, call } from "redux-saga/effects";

import { addItemToCart, getCartItems } from "./cart.utils";
import UserActionTypes from "../user/user.types";

import { clearCart, addItemSucccess, addItemFailure } from "./cart.actions";
import CartActionTypes from "./cart.types";
import { firestore } from "firebase";

function* clearCartOnSignOut() {
  yield put(clearCart());
}

function* addItem({ payload: { item, currentUser } }) {
  try {
    const userId = currentUser.id;

    // Retrieve user cart items from firestore collection
    const cartItems = yield getCartItems(userId);

    const updatedCartItems = yield addItemToCart(cartItems, item);

    // Update users cart on firebase with new updated cart
    yield firestore.collection("Carts").doc(currentUser.id).set({
      cartItems: updatedCartItems,
    });

    // Update cart items on client side
    yield put(addItemSucccess(updatedCartItems));
  } catch (error) {
    yield put(addItemFailure(error));
  }
}

function* onSignOutSuccess() {
  yield takeLatest(UserActionTypes.SIGN_OUT_SUCCESS, clearCartOnSignOut);
}

function* onAddItem() {
  yield takeLatest(CartActionTypes.ADD_ITEM_START, addItem);
}

export function* cartSagas() {
  yield all([call(onSignOutSuccess), call(onAddItem)]);
}
