import CartActionTypes from "./cart.types";

export const toggleCartHidden = () => ({
  type: CartActionTypes.TOGGLE_CART_HIDDEN,
});

export const updateCartSuccessSucccess = (cartItems) => ({
  type: CartActionTypes.UPDATE_CART_SUCCESS,
  payload: cartItems,
});

export const updateCartFailure = (error) => ({
  type: CartActionTypes.UPDATE_CART_FAILURE,
  payload: error,
});

export const addItemStart = (cartItemDetails) => ({
  type: CartActionTypes.ADD_ITEM_START,
  payload: cartItemDetails,
});

export const removeItemStart = (item) => ({
  type: CartActionTypes.REMOVE_ITEM_START,
  payload: item,
});

export const clearItemFromCart = (item) => ({
  type: CartActionTypes.CLEAR_ITEM_FROM_CART,
  payload: item,
});

export const clearCart = () => ({
  type: CartActionTypes.CLEAR_CART,
});
