import CartActionTypes from "./cart.types";

export const toggleCartHidden = () => ({
  type: CartActionTypes.TOGGLE_CART_HIDDEN,
});

export const addItem = (cartItemDetails) => ({
  type: CartActionTypes.ADD_ITEM_START,
  payload: cartItemDetails,
});

export const addItemSucccess = (cartItems) => ({
  type: CartActionTypes.ADD_ITEM_SUCCESS,
  payload: cartItems,
});

export const addItemFailure = (error) => ({
  type: CartActionTypes.ADD_ITEM_FAILURE,
  payload: error,
});

export const removeItem = (item) => ({
  type: CartActionTypes.REMOVE_ITEM,
  payload: item,
});

export const clearItemFromCart = (item) => ({
  type: CartActionTypes.CLEAR_ITEM_FROM_CART,
  payload: item,
});

export const clearCart = () => ({
  type: CartActionTypes.CLEAR_CART,
});
