import { all, takeLatest, put, call, takeEvery } from "redux-saga/effects";

import { addItemToCart, getCartItems, removeItemFromCart } from "./cart.utils";
import UserActionTypes from "../user/user.types";

import {
  clearCart,
  updateCartSuccess,
  updateCartFailure,
} from "./cart.actions";
import CartActionTypes from "./cart.types";
import { firestore } from "../../firebase/firebase.utils";

function* clearCartOnSignOut() {
  yield put(clearCart());
}

function* updateCart({
  payload: {
    item,
    transaction: { type, currentUser },
  },
}) {
  try {
    const userId = currentUser.id;

    // Retrieve user cart items from firestore collection
    const cartItems = yield getCartItems(userId);

    let updatedCartItems = [];

    switch (type) {
      case "add":
        updatedCartItems = yield addItemToCart(cartItems, item);
        break;

      case "remove":
        updatedCartItems = yield removeItemFromCart(cartItems, item);
        break;

      default:
        break;
    }

    // Update users cart on firebase with new updated cart
    yield firestore.collection("Carts").doc(currentUser.id).set({
      cartItems: updatedCartItems,
    });

    // Update cart items on client side
    yield put(updateCartSuccess(updatedCartItems));
  } catch (error) {
    yield put(updateCartFailure(error));
  }
}

function* addItem({ payload: { item, currentUser } }) {
  try {
  } catch (error) {
    yield put(updateCartFailure(error));
  }
}

function* removeItem({ payload: { item, currentUser } }) {
  try {
    const userId = currentUser.id;

    // Retrieve user cart items from firestore collection
    const cartItems = yield getCartItems(userId);

    const updatedCartItems = yield firestore
      .collection("Carts")
      .doc(currentUser.id)
      .set({
        cartItems: updatedCartItems,
      });

    // Update cart items on client side
    yield put(updateCartSuccess(updatedCartItems));
  } catch (error) {
    yield put(updateCartFailure(error));
  }
}

function* onSignOutSuccess() {
  yield takeLatest(UserActionTypes.SIGN_OUT_SUCCESS, clearCartOnSignOut);
}

function* onUpdateCart() {
  yield takeLatest(CartActionTypes.UPDATE_CART_START, updateCart);
}

function* onAddItem() {
  yield takeLatest(CartActionTypes.ADD_ITEM_START, addItem);
}

function* onRemoveItem() {
  yield takeLatest(CartActionTypes.REMOVE_ITEM_START, removeItem);
}

export function* cartSagas() {
  yield all([call(onSignOutSuccess), call(onAddItem), call(onRemoveItem)]);
}
